#!/bin/python3

from pwn import *
import logging
logging.disable()

# Se challenge tcp
HOST = os.environ.get("HOST", "todo.challs.teamitaly.eu")
PORT = int(os.environ.get("PORT", 34001))

chall = remote(HOST, PORT)

# Check challenge
S = ''

E11_coeffs = [[25396642935463135525382193223342560797101605487174235255231092314383562174944, 49475619011029623156553512164871034915606030818467273412265658342516332475296, 0, 24267424610470174292521308378992422082338664553856759099734341657483667524703], [44254721219553949680743538274143037194240144203100055318735453258859463983244, 18545383117476362061987951298149337171728455162038694917819432382341693576430, 0, 55197660504023435387086869245714119826216240210285337594180567617658306423569]]

deg_sigma = 2562

for coef in E11_coeffs:
    for ai in coef:
        chall.sendlineafter(b": ", str(ai).encode())

chall.sendlineafter(b": ", S.encode())
chall.sendlineafter(b": ", str(deg_sigma).encode())

for _ in range(5):
    print(chall.recvline().decode())